function format_debit_card(debit_card) {
  let new_str = ""; //No I18N
  let count = 0;
  for (i = 0; i < debit_card.length; i++) {
    if (count == 4) {
      new_str += " "; //No I18N
      count = 0;
    }
    new_str += debit_card[i];
    count++;
  }
  return new_str;
}
