function getAge(today, dob) {
  if (dob == "-") {
    return "-";
  }
  diff_in_year = today.getFullYear() - dob.getFullYear();
  diff_in_month = today.getMonth() - dob.getMonth();
  if (diff_in_year < 0) {
    return "-";
  } else {
    if (diff_in_month < 0) {
      diff_in_year -= 1;
      if (diff_in_year < 0) {
        return "-";
      }
      diff_in_month = 12 + diff_in_month;
      return `(${diff_in_year}Y, ${diff_in_month}M)`;
    } else {
      return `(${diff_in_year}Y, ${diff_in_month}M)`;
    }
  }
}
